    function getRandomANHQV() {
      const season = Math.floor(Math.random() * 4);
      const chapter = Math.floor(Math.random() * chapters[season].length);
    
      const titleElement = document.getElementById('title');
      titleElement.classList.remove('random');
      const chapterToShow = (chapter+1).toString().length === 1 ? `0${chapter + 1}` : chapter + 1;
      titleElement.innerHTML = `T${season + 1}xC${chapterToShow}: ${chapters[season][chapter].title}`;
    
      const descriptionElement = document.getElementById('description');
      descriptionElement.classList.remove('random');
      descriptionElement.innerHTML = chapters[season][chapter].description;

      const imageElement = document.getElementById('chapterImage');
      imageElement.classList.remove('random');
      imageElement.src = chapters[season][chapter].image;

      setTimeout(() => titleElement.classList.add('random'), 5);
      setTimeout(() => descriptionElement.classList.add('random'), 5);
      setTimeout(() => imageElement.classList.add('random'), 5);

    //   document.getElementById('footer').removeAttribute('hidden');
      document.getElementById('footer').setAttribute('class', 'visible');
    }
    